mapper.select:
select 

from vote
where user_id = #{userId,jdbcType=INTEGER}
    and answer_id = #{answerId,jdbcType=INTEGER}

mapper.delete:
delete from vote
where user_id = #{userId,jdbcType=INTEGER}
    and answer_id = #{answerId,jdbcType=INTEGER}

mapper.update:
update vote

    
        mode = #{mode,jdbcType=BIT},
    

where user_id = #{userId,jdbcType=INTEGER}
    and answer_id = #{answerId,jdbcType=INTEGER}

update vote
set mode = #{mode,jdbcType=BIT}
where user_id = #{userId,jdbcType=INTEGER}
    and answer_id = #{answerId,jdbcType=INTEGER}
